		driver.navigate().to("https://acmon.msbte.ac.in/msbteacmon/acmon_s_22_23_bak/outer.php");
		Thread.sleep(3000);

		// System.exit(0);
		driver.findElement(By.xpath("//a[normalize-space()='Log In']")).click();
		Thread.sleep(3000);
		driver.findElement(By.xpath("//span[normalize-space()='Get EIMC Username & Password']")).click();
